import React, { useState } from 'react'
import './App.css';
import axios from 'axios'
function App() {
  const [image, setImg] = useState()
    const [images, setImgs] = useState()
    const handleSubmit = async (e) => {
        e.preventDefault()
        // console.log(image, images)
        const formdata = new FormData();
        console.log("images" , image)
        formdata.append('image', image)
        for (let i = 0; i < images.length; i++) {
            formdata.append('images', images[i])
        }
        console.log("images" , formdata)
          try {
      const response = await axios.post('http://localhost:8080/imagesUpload', formdata);
      console.log('product res', response.status)

      if (response.status === 201) {
          console.log('Product added successfully');
      } else {
          console.error('Error adding product');
      }
  } catch (error) {
      console.error('Error adding product:', error.message);

  }

    }
    return (
        <form onSubmit={handleSubmit} encType='multipart/form-data'>
            <div className="img">
                <input type="file" name="img" id="" onChange={(e) => (setImg( e.target.files))} />
                <input type="file" name="imgs" multiple id="" onChange={(e) => (setImgs( e.target.files))} />
                <button type="submit">Submit</button>
            </div>
        </form>
    )
}


export default App;
